import React from "react";
import ApexCharts from "apexcharts";
import "./MainPage.css";

var options = {
  chart: {
    type: "donut",
  },
  series: [44, 55, 13, 33],
  chartOptions: {
    labels: ["Apple", "Mango", "Orange", "Watermelon"],
  },
  plotOptions: {
    pie: {
      customScale: 0.3,
    },
  },
};

var chart = new ApexCharts(document.querySelector("#chart"), options);
chart.render();

function MainPage() {
  return <div id="chart"></div>;
}

export default MainPage;
